mapper.select:
SELECT a.id as id, a.name as name, a.age as age, a.address as address, a.create_date as createDate, a.mobile as mobile, a.email as email, a.token as token, a.unique_id as uniqueid FROM order_user a


        and unique_id = #{uniqueid}
        and mobile = #{mobile}
        and email = #{email}
        and token = #{token}

SELECT a.id as id, a.name as name, a.corp_restaurant as corpRestaurant, a.revision_id as revisionId, a.week_date as weekDate FROM order_menu a


        and id = #{id}
        and week_date = #{weekDate}
